    private void ControlClick(string type)
    {
        switch(type.ToUpper())
        {
            case "PLAY":
                sim.PlayPause();
                controls[controls.IndexOf(type)] = "Pause";
                break;
            case "PAUSE":
                sim.PlayPause();
                controls[controls.IndexOf(type)] = "Play";
                break;
            case "CLEAR":
                sim.Clear();
                break;
            case "SAVE":
                sim.Save();
                break;
            case "RESET":
                sim.Reset();
                break;
            default:
                break;
        }
    }

    protected override void OnAfterRender(bool firstRender)
    {
